mapper.insert:
insert
	into tb_feed
		(
			  feed_no
			, mem_no
			, feed_title
			, feed_contents
		)
		values (
			 seq_feedno.nextval
			, #{memNo}
			, #{feedTitle}
			, #{feedContents}
			)

	insert 
        into tb_feed_img
        (
              feed_img_no
            , feed_no
            , img_original_name1
            , img_change_name1
            , img_original_name2
            , img_change_name2
            , img_original_name3
            , img_change_name3
            , img_original_name4
            , img_change_name4
        )  
        values
        (
              seq_feedimgno.nextval
            , seq_feedno.currval
            , #{imgOriginalName1}
            , #{imgChangeName1}
            , #{imgOriginalName2}
            , #{imgChangeName2}
            , #{imgOriginalName3}
            , #{imgChangeName3}
            , #{imgOriginalName4}
            , #{imgChangeName4}
        )

mapper.update:
		update 
        	tb_feed
        set 
              feed_title = #{feedTitle}
            , feed_contents = #{feedContents}
            , feed_modify = sysdate
        where feed_no = #{feedNo}

	update 
        tb_feed_img
    set
            img_original_name1 = #{imgOriginalName1}
            , img_change_name1 = #{imgChangeName1}
            , img_original_name2 = #{imgOriginalName2}
            , img_change_name2 = #{imgChangeName2}
            , img_original_name3 = #{imgOriginalName3}
            , img_change_name3 = #{imgChangeName3}
            , img_original_name4 = #{imgOriginalName4}
            , img_change_name4 = #{imgChangeName4}
    where feed_no = #{feedNo}

mapper.select:
select 
	count(*) 
from tb_like
where feed_no = #{feedNo}
and mem_no = #{memNo}

mapper.insert:
insert 
 into tb_reply
(
	  reply_no
	, feed_no
	, mem_no
)
values
(
	  seq_likeno.nextval
	, #{feedNo}
	, #{memNo}
)
